INSERT INTO CV(cv_nom_poste, cv_nom_employeur, cv_ville, cv_annee_debut, cv_annee_fin, cv_description_poste, cv_utilisateur) VALUES ("Stagiaire aux archives pontificales du Saint-Siège", "Archives pontificales du Saint-Siège", "Rome", 2020, 2020, "Pour le stage de M2, je pars en Italie à Rome aux archives pontificales. J'aiderai à la mise en ligne des plus beaux fonds, et à leur valorisation auprès du plus grand nombre.", 7)
