INSERT INTO Post(post_titre, post_message, post_date, html, post_auteur, post_indexation) VALUES ("bibliothèques", "Bonjour, \n La création de *bibliothèques numériques* est aujourd'hui très répandue. Est-ce une tendance dans laquelle il faut s'engager? Ou bien n'est-ce qu'un phénomène qui risque de disparaître dans quelques années. Je me pose cette question car de toutes petites bibliothèques s'engagent dans cette aventure avec ~~notre~~ l'argent public, et on peut s'interroger sur leur utilité dans des communes de 1000 habitants.", "2020-02-16 23:18:35.258741", "<p>Bonjour, </p><p> La création de <em>bibliothèques numériques</em> est aujourd'hui très répandue. Est-ce une tendance dans laquelle il faut s'engager? Ou bien n'est-ce qu'un phénomène qui risque de disparaître dans quelques années. Je me pose cette question car de toutes petites bibliothèques s'engagent dans cette aventure avec <del>notre</del> l'argent public, et on peut s'interroger sur leur utilité dans des communes de 1000 habitants.</p>", 1, "BIBLIOTHEQUES")
